script:
frappe.call({
method: "appointments_management.controllers.api.my_appointment",
callback: function (response) {
    if (response.message) {
        const data = response.message;
        console.log("API Data:", data);

        const tableBody = root_element.querySelector(".appointment-table-body");
        tableBody.innerHTML = "";  

        data.forEach((item, index) => {
            const row = document.createElement("tr");

            // Check if the status is saved in localStorage, and apply the class accordingly
            if (localStorage.getItem(item.name) === "Accepted") {
                // row.classList.add("bg-success", "text-white");
            } else if (localStorage.getItem(item.name) === "Rejected") {
                // row.classList.add("bg-danger", "text-white");
            }

            const status = localStorage.getItem(item.name) || "Pendding";
            // Determine background color for status cell based on status
            let statusBgClass = "";
            if (status === "Accepted") {
                statusBgClass = "bg-success text-white";
            } else if (status === "Rejected") {
                statusBgClass = "bg-danger text-white";
            } else {
                statusBgClass = "bg-warning text-white";  
            }


            row.innerHTML = `
                <th scope="row" class="bg-light" style="color:black;">${index + 1}</th>
                <td>${item.patient || "N/A"}</td>
                <td>${item.pataient_age || "N/A"}</td>
                <td>${item.gender || "N/A"}</td>
                <td>${item.datetime || "N/A"}</td>
                <td>${item.doctor_name || "N/A"}</td>
                <td  class="text-center" >${item.doctor_fees || "N/A"}</td>
                <td class="status-cell text-center ${statusBgClass}">${status}</td>
                <td class="flex bg-light justify-center" style="gap:10px;">
                    <div class="dropdown">
                        <button class="btn btn-secondary dropdown-toggle dropdownMenuButton" type="button" aria-haspopup="true" aria-expanded="false">
                            ...
                        </button>
                        <div class="dropdown-menu" aria-labelledby="dropdownMenuButton">
                            <button class="btn dropdown-item btn-primary send-email-btn" ${status === "Accepted" ? 'disabled' : ''}>Accept</button>
                            <button class="btn dropdown-item btn-warning de">Delete</button>
                            <button class="btn dropdown-item btn-warning reject-email-btn" ${status === "Rejected" ? 'disabled' : ''}>Reject</button>
                        </div>
                    </div>
                </td>
            `;

            tableBody.appendChild(row);


                const dropdownButton = row.querySelector('.dropdownMenuButton');
                const dropdownMenu = row.querySelector('.dropdown-menu');

                // Toggle dropdown on button click
                if (dropdownButton && dropdownMenu) {
                    dropdownButton.addEventListener('click', function(event) {
                        event.stopPropagation(); // Prevent propagation to document click handler
                        dropdownMenu.classList.toggle('show'); // Toggle dropdown visibility
                    });
                }

                // Close dropdown if clicked outside
                document.addEventListener('click', function(event) {
                    // If the click is outside the dropdown button or the dropdown menu, hide it
                    if (!dropdownButton.contains(event.target) && !dropdownMenu.contains(event.target)) {
                        dropdownMenu.classList.remove('show');
                    }
                });

                // Add event listeners for the buttons
                const deletes = row.querySelector(".de");
                if (deletes) {
                    deletes.addEventListener("click", function () {
                        deleteAppointment(item.name, row);
                    });
                }

                const sendEmailBtn = row.querySelector(".send-email-btn");
                sendEmailBtn.addEventListener("click", function () {
                    if (sendEmailBtn.disabled) return;
                    sendEmail(item.email, item.patient, item.doctor_name, item.datetime, "accept");
                    acceptAppointment(item.name, row, sendEmailBtn);
                });

                const rejectEmailBtn = row.querySelector(".reject-email-btn");
                rejectEmailBtn.addEventListener("click", function () {
                    if (rejectEmailBtn.disabled) return;
                    sendEmail(item.email, item.patient, item.doctor_name, item.datetime, "reject");
                    rejectAppointment(item.name, row, rejectEmailBtn);
                });
            });
        }
    },
});

// Function to send email (accept or reject)
function sendEmail(email, patient, doctorName, datetime, action) {
    if (!email) {
        frappe.msgprint("No email address available for this appointment.");
        return;
    }

    let method = action === "accept" ? "appointments_management.controllers.api.send_accept_email" : "appointments_management.controllers.api.send_reject_email";

    frappe.call({
        method: method,
        args: {
            recipient_email: email,
            patient: patient,
            doctor_name: doctorName,
            datetime: datetime,
        },
        callback: function (response) {
            if (response.message === "success") {
                frappe.msgprint(action === "accept" ? "Appointment accepted and email sent!" : "Rejection email sent!");
            } else {
                frappe.msgprint("Failed to send email. Please try again.");
            }
        },
    });
}
function acceptAppointment(appointmentName, row, sendEmailBtn) {
    frappe.call({
        method: "appointments_management.controllers.api.accept_appointment", 
        args: {
            appointment_name: appointmentName,
            status: "Accepted", 
        },
        callback: function (response) {
            if (response.message === "success") {
                frappe.msgprint("Appointment accepted successfully!");
                localStorage.setItem(appointmentName, "Accepted");  
                sendEmailBtn.disabled = true;  
                const statusCell = row.querySelector(".status-cell");
                if (statusCell) {
                    statusCell.innerText = "Accepted";  
                }
            } else {
                frappe.msgprint("Failed to accept appointment. Please try again.");
            }
        },
    });
}

function rejectAppointment(appointmentName, row, rejectEmailBtn) {
    frappe.call({
        method: "appointments_management.controllers.api.reject_appointment", 
        args: {
            appointment_name: appointmentName,
            status: "Rejected", 
        },
        callback: function (response) {
            if (response.message === "success") {
                frappe.msgprint("Appointment rejected successfully!");
                localStorage.setItem(appointmentName, "Rejected"); // Save the status to localStorage
                rejectEmailBtn.disabled = true; // Disable the button after it has been clicked
                const statusCell = row.querySelector(".status-cell");
                if (statusCell) {
                    statusCell.innerText = "Rejected"; // Update status in the table
                }
            } else {
                frappe.msgprint("Failed to reject appointment. Please try again.");
            }
        },
    });
}

function deleteAppointment(appointmentName, row) {
    frappe.call({
        method: "appointments_management.controllers.api.delete_appointment",  
        args: {
            appointment_name: appointmentName,
        },
        callback: function (response) {
            if (response.message) {
                frappe.msgprint({
                    title: "Success",
                    indicator: "green",
                    message: "Appointment deleted successfully!"
                });
                row.remove();  
            } else {
                frappe.msgprint({
                    title: "Error",
                    indicator: "red",
                    message: "Failed to delete appointment. Please try again."
                });
            }
        },
    });
}

style:
/* Ensure the dropdown menu is hidden by default */
.dropdown-menu {
    display: none; /* Hidden by default */
    position: absolute;
    z-index: 1050; 
    background-color: #fff;
    border: 1px solid rgba(0, 0, 0, 0.15);
    border-radius: 0.25rem;
    box-shadow: 0 0.5rem 1rem rgba(0, 0, 0, 0.15);
    min-width: 10rem;
    right: 0; /* Ensure the dropdown aligns to the left side */
    transform: translateX(-100%); /* Shift it fully to the left */
}

/* Show dropdown when 'show' class is added */
.dropdown-menu.show {
    display: block;
}

/* Optional: Style dropdown button for better visibility */
.dropdown-toggle {
    padding: 0.375rem 0.75rem;
    font-size: 1rem;
    line-height: 1.5;
    background-color: #6c757d;
    color: white;
    cursor: pointer;
}

/* Style for dropdown items */
.dropdown-item {
    display: block;
    width: 100%;
    padding: 0.375rem 1.25rem;
    clear: both;
    font-weight: 400;
    color: #212529;
    text-align: inherit;
    background-color: transparent;
    border: 0;
    cursor: pointer;
}

.dropdown-item:hover {
    background-color: #f8f9fa;
}

/* Optional: Style disabled items */
.dropdown-item:disabled {
    background-color: #e9ecef;
    color: #6c757d;
    cursor: not-allowed;
}
