script:
frappe.call({
    method: "appointments_management.controllers.api.my_appointment",
    callback: function (response) {
        if (response.message) {
            const data = response.message;
            console.log("API Data:", data);

            const tableBody = root_element.querySelector(".appointment-table-body");
            tableBody.innerHTML = "";  

            data.forEach((item, index) => {
                const row = document.createElement("tr");

                row.innerHTML = `
                    <th scope="row">${index + 1}</th>
                    <td>${item.patient || "N/A"}</td>
                    <td>${item.pataient_age }</td>
                    <td>${item.formatteddatetime || "N/A"}</td>
                    <td>${item.doctor_name || "N/A"}</td>
                    <td>${item.doctor_fees || "N/A"}</td>
                    <td>
                        <button class="btn btn-primary btn-sm">Edit</button>
                        <button class="btn btn-danger btn-sm delete-btn">Delete</button>
                    </td>
                `;

                tableBody.appendChild(row);

             const deleteBtn = row.querySelector(".delete-btn");
                deleteBtn.addEventListener("click", function () {
                    deleteAppointment(item.name, row);
                });
            });

        }
    },
});

 function deleteAppointment(appointmentId, row) {
    frappe.call({
        method: "appointments_management.controllers.api.delete_appointment", // Path to your delete function
        args: {
            appointment_id: appointmentId
        },
        callback: function (response) {
            if (response.message.status === "success") {
                // Remove the row from the DOM after successful deletion
                row.remove();
                frappe.msgprint("Appointment deleted successfully!");
            } else {
                frappe.msgprint("Failed to delete the appointment: " + response.message.message);
            }
        }
    });
}
